import './Widget.css';
import {useState} from "react";

export const Widget = ({handleUpdateLayers}) => {
    const [inputValues, setInputValues] = useState({
        intensity: 1,
        radius: 1,
        threshold: 1,
    })


    const handleInputsChange = (event, type) => {
        handleUpdateLayers(+event.target.value, type)
    }

    return (
        <div className="map-widget">
            <div>
                <h3>Change heatmap view</h3>
            </div>
            <div className="map-widget-input">
                <label htmlFor="intensity">Intensity</label>
                <input id="intensity" name="intensity" type="range" min={1} max={9} onChange={(event) => handleInputsChange(event, 'intensity')}/>
            </div>
            <div className="map-widget-input">
                <label htmlFor="radius">Radius</label>
                <input id="radius" name="radius" type="range" min={1} max={9} onChange={(event) => handleInputsChange(event, 'radiusPixels')}/>
            </div>
            <div className="map-widget-input">
                <label htmlFor="threshold">Threshold</label>
                <input id="threshold" name="threshold" type="range" min={1} max={9} onChange={(event) => handleInputsChange(event, 'threshold')}/>
            </div>
        </div>
    )
}
