const DATA_URL =
    'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/screen-grid/uber-pickup-locations.json'; // eslint-disable-line

const INITIAL_VIEW_STATE = {
    longitude: -73.75,
    latitude: 40.73,
    zoom: 9,
    maxZoom: 16,
    pitch: 0,
    bearing: 0
};

const MAP_STYLE = 'https://basemaps.cartocdn.com/gl/dark-matter-nolabels-gl-style/style.json';

export const MapBox = ({
                           data = DATA_URL,
                           intensity = 1,
                           threshold = 0.03,
                           radiusPixels = 30,
                           mapStyle = MAP_STYLE
                       }) => {

    const layers = [
        new HeatmapLayer({
            data,
            id: 'heatmp-layer',
            pickable: false,
            getPosition: d => [d[0], d[1]],
            getWeight: d => d[2],
            radiusPixels,
            intensity,
            threshold
        })
    ];

    return (
        <DeckGL initialViewState={INITIAL_VIEW_STATE} controller={true} layers={layers}>
            <Map reuseMaps mapboxAccessToken={'pk.eyJ1IjoibWFrcy1tMTMiLCJhIjoiY2xlNXoxb2d0MGh4MjNwdnpqbGo0MnBodCJ9.tsyFzruRVKGCF-mK_kteRg'} mapStyle={mapStyle} preventStyleDiffing={true} />
        </DeckGL>
    )
}
