import './Widget.css';

function debounce(func, timeout = 300){
    let timer;
    return (...args) => {
        clearTimeout(timer);
        timer = setTimeout(() => { func.apply(this, args); }, timeout);
    };
}

export const Widget = ({handleUpdateLayers}) => {
    const handleInputsChange = (event, type) => {
        debounce(handleUpdateLayers(event.target.value, type), 5000);
    }

    return (
        <div className="map-widget">
            <div>
                <h3>Change heatmap view</h3>
            </div>
            <div className="map-widget-input">
                <label htmlFor="intensity">Intensity</label>
                <input id="intensity" name="intensity" type="range" min={0} max={10} onChange={(event) => handleInputsChange(event, 'intensity')}/>
            </div>
            <div className="map-widget-input">
                <label htmlFor="radius">Radius</label>
                <input id="radius" name="radius" type="range" min={0} max={10} onChange={(event) => handleInputsChange(event, 'radiusPixels')}/>
            </div>
            <div className="map-widget-input">
                <label htmlFor="threshold">Threshold</label>
                <input id="threshold" name="threshold" type="range" min={0} max={10} onChange={(event) => handleInputsChange(event, 'threshold')}/>
            </div>
        </div>
    )
}
