import './Widget.css';

export const Widget = ({handleUpdateLayers}) => {

    const handleIntensity = (event) => {
        console.log(event.target.value);
    }

    const handleRadius = (event) => {
        console.log(event);
        console.log(event.target.value);
    }

    const handleThreshold = (event) => {
        console.log(event.target.value);
    }

    return (
        <div className="map-widget">
            <div>
                <label htmlFor="intensity">Intensity</label>
                <input id="intensity" name="intensity" type="range" min={0} max={10} onChange={(event) => handleIntensity(event, 'intensity')}/>
            </div>
            <div>
                <label htmlFor="radius">Radius</label>
                <input id="radius" name="radius" type="range" min={0} max={10} onChange={(event) => handleRadius(event, 'radius')}/>
            </div>
            <div>
                <label htmlFor="threshold">Threshold</label>
                <input id="threshold" name="threshold" type="range" min={0} max={10} onChange={(event) => handleThreshold(event, 'threshold')}/>
            </div>
        </div>
    )
}
