import {useEffect, useState} from "react";
import DeckGL from "@deck.gl/react";
import { Map } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import {HeatmapLayer} from '@deck.gl/aggregation-layers';
import {Widget} from "./components";
import { mapDummyData } from "./mapData";

const DATA_URL =
    'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/screen-grid/uber-pickup-locations.json';// eslint-disable-line

const ALT_DATA = 'https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_month.geojson';

console.log(ALT_DATA, 'alt data');

const API_KEY = 'pk.eyJ1IjoibWFrcy1tMTMiLCJhIjoiY2xlNXoxb2d0MGh4MjNwdnpqbGo0MnBodCJ9.tsyFzruRVKGCF-mK_kteRg';

const INITIAL_VIEW_STATE = {
    longitude: -73.75,
    latitude: 40.73,
    zoom: 9,
    maxZoom: 16,
    pitch: 0,
    bearing: 0
};

const MAP_STYLE = 'https://basemaps.cartocdn.com/gl/dark-matter-nolabels-gl-style/style.json';

export const MapBox = ({
                           // data = ALT_DATA,
                           intensity = 2,
                           threshold = 0.03,
                           radiusPixels = 30,
                           mapStyle = MAP_STYLE,
                           showWidget = false,
                       }) => {
    const [mapData, setMapData] = useState([]);
    useEffect(() => {
         fetch('https://earthquake.usgs.gov/earthquakes/feed/v1.0/summary/all_month.geojson').then(data => data.json())
            .then(data => {
                const coordinates = [];
                for(let i=0; i<data.features.length; i++) {
                    coordinates.push([data.features[i].geometry.coordinates[0], data.features[i].geometry.coordinates[1], Math.floor(Math.random() * (7 - 1 + 1) + 1)])
                }
                // for(let item in data.features) {
                //     coordinates.push(item.geometry.coordinates[0], item.geometry.coordinates[1], Math.floor(Math.random() * (7 - 1 + 1) + 1))
                // }
                console.log(coordinates);
            })
    }, [])



    const DEFAULT_LAYER_VALUES = {
        data: mapData,
        id: 'heatmp-layer',
        pickable: false,
        debounceTimeout: 5000,
        getPosition: d => [d[0], d[1]],
        getWeight: d => d[2],
        radiusPixels,
        intensity,
        threshold,
    }

    const initialLayer = new HeatmapLayer({
        ...DEFAULT_LAYER_VALUES,
    })

    const [layers, setLayers] = useState([initialLayer]);

    const handleLayers = (value, type) => {
        DEFAULT_LAYER_VALUES[type] = value;
        const newLayer = new HeatmapLayer(DEFAULT_LAYER_VALUES);
        setLayers([newLayer]);
    }

    return (
        <>
            {showWidget && <Widget handleUpdateLayers={handleLayers}/>}
            <DeckGL initialViewState={INITIAL_VIEW_STATE} controller={true} layers={layers}>
                <Map reuseMaps mapboxAccessToken={API_KEY} mapStyle={mapStyle} preventStyleDiffing={true} />
            </DeckGL>
        </>
    )
}
