import DeckGL from "@deck.gl/react";
import { Map } from 'react-map-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import {HeatmapLayer} from '@deck.gl/aggregation-layers';
import {useState} from "react";
import {Widget} from "./components";

const DATA_URL =
    'https://raw.githubusercontent.com/visgl/deck.gl-data/master/examples/screen-grid/uber-pickup-locations.json'; // eslint-disable-line

const API_KEY = 'pk.eyJ1IjoibWFrcy1tMTMiLCJhIjoiY2xlNXoxb2d0MGh4MjNwdnpqbGo0MnBodCJ9.tsyFzruRVKGCF-mK_kteRg';

const INITIAL_VIEW_STATE = {
    longitude: -73.75,
    latitude: 40.73,
    zoom: 9,
    maxZoom: 16,
    pitch: 0,
    bearing: 0
};


const MAP_STYLE = 'https://basemaps.cartocdn.com/gl/dark-matter-nolabels-gl-style/style.json';

export const MapBox = ({
                           data = DATA_URL,
                           intensity = 2,
                           threshold = 0.03,
                           radiusPixels = 30,
                           mapStyle = MAP_STYLE
                       }) => {
    const DEFAULT_LAYER_VALUES = {
        data,
        id: 'heatmp-layer',
        pickable: false,
        getPosition: d => [d[0], d[1]],
        getWeight: d => d[2],
        radiusPixels,
        intensity,
        threshold
    }


    const initialLayer = new HeatmapLayer({
        ...DEFAULT_LAYER_VALUES,
    })

    const [layers, setLayers] = useState([initialLayer]);

    const handleLayers = (type, value) => {
        DEFAULT_LAYER_VALUES[type] = value;
        const newLayer = new HeatmapLayer(DEFAULT_LAYER_VALUES);
        setLayers([newLayer]);
    }


    return (
        <DeckGL initialViewState={INITIAL_VIEW_STATE} controller={true} layers={layers}>
            <Widget handleUpdateLayers={handleLayers}/>
            <Map reuseMaps mapboxAccessToken={API_KEY} mapStyle={mapStyle} preventStyleDiffing={true} />
        </DeckGL>
    )
}
